import React, { useEffect, useState } from 'react'
import { useStateValue } from "../context/StateProvider";
import { motion } from "framer-motion";
import { changinUserRole, getAllUsers } from "../api/index";
import { actionType } from "../context/reducer";
import moment from "moment";


const DashboardUsers = () => {
  const [{ allUsers }, dispatch] = useStateValue();

  useEffect(() => {
    if (!allUsers) {
      getAllUsers().then((data) => {
        dispatch({
          type: actionType.SET_ALL_USERS,
          allUsers: data.data,
        });
      });
    }
  }, []);

  return (
    <div className='w-full p-4 flex items-center justify-center flex-col'>
      <div className=' relative w-full py-12 min-h-[400px] overflow-x-scroll my-4 flex flex-col items-center justify-start 
      p-4 border border-gray-300 rounded-md gap-3'>
        <div className=' absolute top-4 left-4'>
          <p className=' text-sm font-semibold'>
            Count: <span className='text-xl font-bold text-textColor'>{allUsers?.length}</span>
          </p>
        </div>

        <div className='w-full min-w-[750px] flex items-center justify-between'>
          <p className='text-sm text-textColor font-semibold w-275 min-w-[160px] text-center'>Image</p>
          <p className='text-sm text-textColor font-semibold w-275 min-w-[160px] text-center'>Name</p>
          <p className='text-sm text-textColor font-semibold w-275 min-w-[160px] text-center'>Email</p>
          <p className='text-sm text-textColor font-semibold w-275 min-w-[160px] text-center'>Verified</p>
          <p className='text-sm text-textColor font-semibold w-275 min-w-[160px] text-center'>Created</p>
          <p className='text-sm text-textColor font-semibold w-275 min-w-[160px] text-center'>Role</p>
        </div>

        {
          allUsers && (
            allUsers?.map((data, index) => (
              <DashboardUserCard data={data} index={index} />
            ))
          )
        }
      </div>
    </div>
  )
};

export const DashboardUserCard = ({ data, index }) => {

  const createdAt = moment(new Date(data.createdAt)).format("MMMM Do YYYY")

  const [isUserRoleUpdated, setIsUserRoleUpdated] = useState(false)

  const [{ allUsers, user }, dispatch] = useStateValue();

  const updateUserRole = (userId, role) => {
    changinUserRole(userId, role).then((res) => {
      if(res){
        getAllUsers().then((data) => {
          dispatch({
            type: actionType.SET_ALL_USERS,
            allUsers: data
          })
        })
      }
    })
  }


  return (
    <motion.div key={index} className='relative w-full rounded-md flex items-center justify-between py-4 bg-lightOverlay cursor-pointer
    hover:bg-card hover:shadow-md'>
      <div className='w-275 min-w-[160px] flex items-center justify-center'>
        <img src={data.imageURL} alt="" className='w-10 h-10 object-cover rounded-md min-w-[40px] shadow-md' referrerPolicy='no-referrer' />
      </div>

      <p className=' text-base text-textColor w-275 min-w-[160px] text-center'>{data.name}</p>
      <p className=' text-base text-textColor w-275 min-w-[160px] text-center'>{data.email}</p>
      <p className=' text-base text-textColor w-275 min-w-[160px] text-center'>{data.email_verfied ? "True" : "False"}</p>
      <p className=' text-base text-textColor w-275 min-w-[160px] text-center'>{createdAt}</p>

      <div className=' w-275 min-w-[160px] text-center flex items-center justify-center gap-6 relative'>
        <p className=' text-base text-textColor text-center'>{data.role}</p>

        {
          data._id !== user?.user._id && (
            <motion.p whileTap={{ scale: 0.75 }} className=' text-[10px] font-semibold text-textColor px-1 bg-purple-200 
            rounded-sm hover:shadow-md' onClick={() => setIsUserRoleUpdated(true)}>
              Change role
            </motion.p>
          )
        }

        {isUserRoleUpdated && (
          <motion.div
            initial={{ opacity: 0, scale: 0.05 }}
            animate={{ opacity: 1, scale: 1}}
            exit={{ opacity: 0, scale: 0.5 }}
            className='absolute z-10 top-6 right-4 p-4 flex items-start flex-col gap-4 bg-white shadow-xl rounded-md'>
            <p className=' text-textColor text-xs font-semibold'>
              Do you want to change users role to <span className='font-bold'>{data.role === "admin" ? "memder" : "admin"} ?</span>
            </p>

            <div className='w-full flex items-center justify-between' >
              <motion.button whileTap={{ scale: 0.75 }} className='outline-none border-none text-xs  px-3 py-1 rounded-md 
              bg-blue-400 text-black hover:shadow-md'
              onClick={() =>  updateUserRole(data._id, data.role === "admin" ? "member" : "admin")}>
                Yes
              </motion.button>

              <motion.button whileTap={{ scale: 0.75 }} className='outline-none border-none text-xs  px-3 py-1 rounded-md 
              bg-gray-400 text-black hover:shadow-md' onClick={() => setIsUserRoleUpdated(false)}>
                No
              </motion.button>
            </div>
          </motion.div>
        )}
      </div>
    </motion.div>
  )
}


export default DashboardUsers
