import React from 'react'
import { BsEmojiWink } from "react-icons/bs"

const Alert = ({type}) => {
    return (
        <div className={`fixed top-12 rigth-12 p-4 rounded-md backdrop-blur-md flex items-center justify-center shadow-md
        ${type === "success" &&  "bg-green-500"}
        ${type === "danger" && "bg-red-500"}`}>
            {type === "success" && (
                <div className='flex items-center justify-center gap-4'>
                    <BsEmojiWink className='text-3xl text-primary' />
                    <p className='tex-xl font-semibold'>Data saved</p>
                </div>
            )}
            {type === "danger" && (
                <div className='flex items-center justify-center gap-4'>
                    <BsEmojiWink className='text-3xl text-primary' />
                    <p className='tex-xl font-semibold'>Data not saved</p>
                </div>
            )}
        </div>
    )
}

export default Alert;
