import React from 'react'
import { BsEmojiWink } from "react-icons/bs"
import {motion} from "framer-motion";

const Alert = ({type}) => {
    return (
        <motion.div 
        initial={{translateX: 100, opacity: 0}}
        animate={{translateX: 0, opacity: 1}}
        exit={{translateX: 100, opacity: 0}}
        className={`fixed top-12 rigth-12 px-4 py-2 rounded-md backdrop-blur-md flex items-center justify-center shadow-xl
        ${type === "success" &&  "bg-green-500"}
        ${type === "danger" && "bg-red-500"}`}>
            {type === "success" && (
                <div className='flex items-center justify-center gap-4'>
                    <BsEmojiWink className='text-3xl text-primary' />
                    <p className='tex-xl font-semibold'>Data saved</p>
                </div>
            )}
            {type === "danger" && (
                <div className='flex items-center justify-center gap-4'>
                    <BsEmojiWink className='text-3xl text-primary' />
                    <p className='tex-xl font-semibold'>Data not saved</p>
                </div>
            )}
        </motion.div>
    )
}

export default Alert;
