import React, { useState } from 'react'
import { NavLink } from "react-router-dom";
import { IoAdd, IoPause, IoPlay, IoTrash } from "react-icons/io5";

const DashboardSongs = () => {

  const [songFilter, setsongFilter] = useState("");
  const [isForcus, setIsForcus] = useState(false);


  return (
    <div className='w-full p-4 flex items-center justify-center flex-col'>
      <div className='w-full flex items-center justify-center gap-20'>
        <NavLink to={"/dashboard/newSong"}>
          <IoAdd />
        </NavLink>

        <input type="text" placeholder='Search:' value={songFilter}
          className={`w-52 px-4 py-1 border ${isForcus ? "border-gray-500 shadow-md" : "border-gray-300"} 
          rounded-md bg-transparent outline-none duration-150 transition-all ease-in-out text-base text-textColor font-semibold`}
          onChange={(event) => setsongFilter(event.target.value)} 
          onBlur={() => {}}
          onFocus={() => setIsForcus(true)}/>
      </div>
    </div>
  )
}

export default DashboardSongs
