import React, { useEffect } from 'react'
import { useStateValue } from "../context/StateProvider";
import { getAllAlbums, getAllArtist, getAllSongs, getAllUsers } from "../api";
import {actionType} from "../context/reducer";

export const DashboardCard = ({ icon, name, count }) => {

  return (
    <div className=' p-4 min-w-40 gap-3 h-auto rounded-lg shadow-md bg-blue-500'>
      {icon}
      <p className='text-xl text-textColor font-semibold'>{name}</p>
      <p className='text-xl text-textColor'>{count}</p>

    </div>
  )
}

const DashboardHome = () => {

  const  [{allUsers, allSongs, allArtists, allAlbums}, dispatch] = useStateValue();

  useEffect(() => {
    if (!allUsers) {
      getAllUsers().then((data) => {
        dispatch({
          type: actionType.SET_ALL_USERS,
          allUsers: data.data,
        });
      });
    }
    if (!allSongs) {
      getAllSongs().then((data) => {
        dispatch({
          type: actionType.SET_ALL_SONGS,
          allSongs: data.data,
        });
      });
    }

    if (!allArtists) {
      getAllArtist().then((data) => {
        dispatch({ type: actionType.SET_ARTISTS, allArtists: data.data });
      });
    }

    if (!allAlbums) {
      getAllAlbums().then((data) => {
        dispatch({ type: actionType.SET_ALL_ALBUMNS, allAlbums: data.data });
      });
    }
  }, [])

  return (
    <div className="w-full p-6 flex items-center justify-evenly flex-wrap">
      <DashboardCard name={"Users"} count={allUsers?.length > 0 ? allUsers?.length : 0}/>
      <DashboardCard name={"Songs"} count={allSongs?.length > 0 ? allSongs?.length : 0}/>
      <DashboardCard name={"Artist"} count={allArtists?.length > 0 ? allArtists?.length : 0}/>
      <DashboardCard name={"Album"} count={allAlbums?.length > 0 ? allAlbums?.length : 0}/>
    </div>
  )
}

export default DashboardHome
